import { Component } from '@angular/core';
@Component({selector:'app-home',templateUrl:'./home.component.html',styleUrls:['./home.component.css']})
export class HomeComponent {
    ngAfterViewInit() {
  const roles = ["Angular Developer", "Full Stack Developer", "UI/UX Enthusiast"];
  let index = 0;
  const typingEl = document.getElementById("typing-text")!;

  function changeText() {
    typingEl.textContent = roles[index];
    index = (index + 1) % roles.length;
  }

  changeText();
  setInterval(changeText, 4000);
}

}
<section class="section hero">
  <div class="animated-bg"></div>

  <div class="hero-text">
    <div class="intro-tag">👋 Hello There!</div>
    <h1>
      I'm <span class="highlight">Bazmi Uddin</span>
    </h1>
    <h2>
      <span id="typing-text"></span>
    </h2>
    <p>
      Passionate about turning complex problems into elegant digital experiences.  
      I build scalable, high-performance web applications using <strong>Angular</strong>, <strong>PHP</strong>,
      and <strong>TypeScript</strong>. My goal is to design software that’s both functional and beautiful.
    </p>
    <div class="hero-buttons">
      <a href="assets/bazmi_NewResume.pdf" target="_blank" class="btn btn-gradient">📄 Resume</a>
      <a routerLink="/projects" class="btn btn-outline">🚀 View Projects</a>
    </div>
  </div>

  <div class="hero-img">
    <div class="img-glow"></div>
    <img src="assets/profile.jpg" alt="Bazmi Uddin" />
  </div>
</section>
